// GroupBy is used to group vertices by one or more fields/columns.
// It is often used with aggregate functions, like: count, max, mean, min, sum.
//
// Example:
//
//	var v []struct {
//		Title string `json:"title,omitempty"`
//		Count int `json:"count,omitempty"`
//	}
//
//	client.Ticket.Query().
//		GroupBy(ticket.FieldTitle).
//		Aggregate(ent.Count()).
//		Scan(ctx, &v)
func (tq *TicketQuery) GroupBy(field string, fields ...string) *TicketGroupBy {
	tq.ctx.Fields = append([]string{field}, fields...)
	grbuild := &TicketGroupBy{build: tq}
	grbuild.flds = &tq.ctx.Fields
	grbuild.label = ticket.Label
	grbuild.scan = grbuild.Scan
	return grbuild
}

// Select allows the selection one or more fields/columns for the given query,
// instead of selecting all fields in the entity.
//
// Example:
//
//	var v []struct {
//		Title string `json:"title,omitempty"`
//	}
//
//	client.Ticket.Query().
//		Select(ticket.FieldTitle).
//		Scan(ctx, &v)
func (tq *TicketQuery) Select(fields ...string) *TicketSelect {
	tq.ctx.Fields = append(tq.ctx.Fields, fields...)
	sbuild := &TicketSelect{TicketQuery: tq}
	sbuild.label = ticket.Label
	sbuild.flds, sbuild.scan = &tq.ctx.Fields, sbuild.Scan
	return sbuild
}

// Aggregate returns a TicketSelect configured with the given aggregations.
func (tq *TicketQuery) Aggregate(fns ...AggregateFunc) *TicketSelect {
	return tq.Select().Aggregate(fns...)
}

func (tq *TicketQuery) prepareQuery(ctx context.Context) error {
	for _, inter := range tq.inters {
		if inter == nil {
			return fmt.Errorf("ent: uninitialized interceptor (forgotten import ent/runtime?)")
		}
		if trv, ok := inter.(Traverser); ok {
			if err := trv.Traverse(ctx, tq); err != nil {
				return err
			}
		}
	}
	for _, f := range tq.ctx.Fields {
		if !ticket.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("ent: invalid field %q for query", f)}
		}
	}
	if tq.path != nil {
		prev, err := tq.path(ctx)
		if err != nil {
			return err
		}
		tq.sql = prev
	}
	return nil
}

func (tq *TicketQuery) sqlAll(ctx context.Context, hooks ...queryHook) ([]*Ticket, error) {
	var (
		nodes       = []*Ticket{}
		withFKs     = tq.withFKs
		_spec       = tq.querySpec()
		loadedTypes = [15]bool{
			tq.withTemplate != nil,
			tq.withCategory != nil,
			tq.withDepartment != nil,
			tq.withTags != nil,
			tq.withRelatedTickets != nil,
			tq.withParentTicket != nil,
			tq.withWorkflowInstances != nil,
			tq.withSLADefinition != nil,
			tq.withSLAViolations != nil,
			tq.withComments != nil,
			tq.withAttachments != nil,
			tq.withNotifications != nil,
			tq.withSLAAlertHistory != nil,
			tq.withApprovalRecords != nil,
			tq.withRootCauseAnalyses != nil,
		}
	)
	if withFKs {
		_spec.Node.Columns = append(_spec.Node.Columns, ticket.ForeignKeys...)
	}
	_spec.ScanValues = func(columns []string) ([]any, error) {
		return (*Ticket).scanValues(nil, columns)
	}
	_spec.Assign = func(columns []string, values []any) error {
		node := &Ticket{config: tq.config}
		nodes = append(nodes, node)
		node.Edges.loadedTypes = loadedTypes
		return node.assignValues(columns, values)
	}
	for i := range hooks {
		hooks[i](ctx, _spec)
	}
	if err := sqlgraph.QueryNodes(ctx, tq.driver, _spec); err != nil {
		return nil, err
	}
	if len(nodes) == 0 {
		return nodes, nil
	}
	if query := tq.withTemplate; query != nil {
		if err := tq.loadTemplate(ctx, query, nodes, nil,
			func(n *Ticket, e *TicketTemplate) { n.Edges.Template = e }); err != nil {
			return nil, err
		}
	}
	if query := tq.withCategory; query != nil {
		if err := tq.loadCategory(ctx, query, nodes, nil,
			func(n *Ticket, e *TicketCategory) { n.Edges.Category = e }); err != nil {
			return nil, err
		}
	}
	if query := tq.withDepartment; query != nil {
		if err := tq.loadDepartment(ctx, query, nodes, nil,
			func(n *Ticket, e *Department) { n.Edges.Department = e }); err != nil {
			return nil, err
		}
	}
	if query := tq.withTags; query != nil {
		if err := tq.loadTags(ctx, query, nodes,
			func(n *Ticket) { n.Edges.Tags = []*TicketTag{} },
			func(n *Ticket, e *TicketTag) { n.Edges.Tags = append(n.Edges.Tags, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withRelatedTickets; query != nil {
		if err := tq.loadRelatedTickets(ctx, query, nodes,
			func(n *Ticket) { n.Edges.RelatedTickets = []*Ticket{} },
			func(n *Ticket, e *Ticket) { n.Edges.RelatedTickets = append(n.Edges.RelatedTickets, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withParentTicket; query != nil {
		if err := tq.loadParentTicket(ctx, query, nodes, nil,
			func(n *Ticket, e *Ticket) { n.Edges.ParentTicket = e }); err != nil {
			return nil, err
		}
	}
	if query := tq.withWorkflowInstances; query != nil {
		if err := tq.loadWorkflowInstances(ctx, query, nodes,
			func(n *Ticket) { n.Edges.WorkflowInstances = []*WorkflowInstance{} },
			func(n *Ticket, e *WorkflowInstance) { n.Edges.WorkflowInstances = append(n.Edges.WorkflowInstances, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withSLADefinition; query != nil {
		if err := tq.loadSLADefinition(ctx, query, nodes, nil,
			func(n *Ticket, e *SLADefinition) { n.Edges.SLADefinition = e }); err != nil {
			return nil, err
		}
	}
	if query := tq.withSLAViolations; query != nil {
		if err := tq.loadSLAViolations(ctx, query, nodes,
			func(n *Ticket) { n.Edges.SLAViolations = []*SLAViolation{} },
			func(n *Ticket, e *SLAViolation) { n.Edges.SLAViolations = append(n.Edges.SLAViolations, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withComments; query != nil {
		if err := tq.loadComments(ctx, query, nodes,
			func(n *Ticket) { n.Edges.Comments = []*TicketComment{} },
			func(n *Ticket, e *TicketComment) { n.Edges.Comments = append(n.Edges.Comments, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withAttachments; query != nil {
		if err := tq.loadAttachments(ctx, query, nodes,
			func(n *Ticket) { n.Edges.Attachments = []*TicketAttachment{} },
			func(n *Ticket, e *TicketAttachment) { n.Edges.Attachments = append(n.Edges.Attachments, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withNotifications; query != nil {
		if err := tq.loadNotifications(ctx, query, nodes,
			func(n *Ticket) { n.Edges.Notifications = []*TicketNotification{} },
			func(n *Ticket, e *TicketNotification) { n.Edges.Notifications = append(n.Edges.Notifications, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withSLAAlertHistory; query != nil {
		if err := tq.loadSLAAlertHistory(ctx, query, nodes,
			func(n *Ticket) { n.Edges.SLAAlertHistory = []*SLAAlertHistory{} },
			func(n *Ticket, e *SLAAlertHistory) { n.Edges.SLAAlertHistory = append(n.Edges.SLAAlertHistory, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withApprovalRecords; query != nil {
		if err := tq.loadApprovalRecords(ctx, query, nodes,
			func(n *Ticket) { n.Edges.ApprovalRecords = []*ApprovalRecord{} },
			func(n *Ticket, e *ApprovalRecord) { n.Edges.ApprovalRecords = append(n.Edges.ApprovalRecords, e) }); err != nil {
			return nil, err
		}
	}
	if query := tq.withRootCauseAnalyses; query != nil {
		if err := tq.loadRootCauseAnalyses(ctx, query, nodes,
			func(n *Ticket) { n.Edges.RootCauseAnalyses = []*RootCauseAnalysis{} },
			func(n *Ticket, e *RootCauseAnalysis) {
				n.Edges.RootCauseAnalyses = append(n.Edges.RootCauseAnalyses, e)
			}); err != nil {
			return nil, err
		}
	}
	return nodes, nil
}

func (tq *TicketQuery) loadTemplate(ctx context.Context, query *TicketTemplateQuery, nodes []*Ticket, init func(*Ticket), assign func(*Ticket, *TicketTemplate)) error {
	ids := make([]int, 0, len(nodes))
	nodeids := make(map[int][]*Ticket)
	for i := range nodes {
		fk := nodes[i].TemplateID
		if _, ok := nodeids[fk]; !ok {
			ids = append(ids, fk)
		}
		nodeids[fk] = append(nodeids[fk], nodes[i])
	}
	if len(ids) == 0 {
		return nil
	}
	query.Where(tickettemplate.IDIn(ids...))
	neighbors, err := query.All(ctx)
	if err != nil {
		return err
	}
	for _, n := range neighbors {
		nodes, ok := nodeids[n.ID]
		if !ok {
			return fmt.Errorf(`unexpected foreign-key "template_id" returned %v`, n.ID)
		}
		for i := range nodes {
			assign(nodes[i], n)
		}
	}
	return nil
}
